import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Container,
  Grid,
  Typography,
  Dialog,
  List,
  ListItem,
  Slide,
} from "@mui/material";
import { useState } from "react";

const Services = () => {
  const [open, setOpen] = useState(false);
  const [service, setService] = useState();
  const services = [
    {
      title: 'Gestión Ambiental',
      about:'Asesoría y consultaría para el diseño e implementación de Sistemas de Gestión Ambiental de acuerdo a la actividad económica de su empresa, soportados en el cumplimiento de los requisitos legales y estrategias innovadoras de gestión.',
      items: ['Estudios de impacto ambiental',
        'Diagnostico Ambiental de alternativas',
        'Planes de manejo ambiental',
        'Programas de mitigación y compensación ambiental',
        'Matriz de requisitos legales',
        'Matriz de aspectos e impactos ambientales',
        'Planes de emergencia y contingencia',
        'Asesoría para tramites de licencias y permisos ambientales',
        'Auditoría interna del SGA',
        'Asesoría en simulacros ambientales']
    }
  ]
  const openDrawer = (open, service) => {
    setOpen(open);
    setService(service);
  };
  return (
    <Container sx={{ py: 2 }}>
      <Typography
        variant="h2"
        sx={{
          textAlign: "center",
          color: "#000032",
          mb: 2,
          fontWeight: "bold",
        }}
      >
        Services
      </Typography>
      <Grid container gap={1} justifyContent="space-around">
        {
          services.map((item,index) => (
            <Grid xs={12} md={5} key={index}>
          <Card elevation={5} sx={{ height: 300, p: 2 }}>
            <CardContent>
              <Typography
                variant="h4"
                sx={{ textAlign: "justify", mb: 2, color: "#f99f00" }}
              >
                dsfsdg
              </Typography>
              <Typography sx={{ textAlign: "justify" }}>
                shhs
              </Typography>
            </CardContent>
            <CardActions
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "flex-end",
              }}
            >
              <Button onClick={() => openDrawer(true, item)}>Ver más</Button>
            </CardActions>
          </Card>
        </Grid>
          ))
        }
        
      </Grid>
      <Dialog fullWidth={true} open={open} onClose={() => setOpen(false)}>
        <Container sx={{p:4}}>
        <Typography
          variant="h4"
          sx={{ textAlign: "justify", mb: 2, color: "#f99f00", textAlign:"center" }}
        >
          {service.title}
        </Typography>
        <Box sx={{ height: 400 }}>
          <List>
            {service.items.map((item,index) => (
              <ListItem key={index}>{item}</ListItem>
            ))}
          </List>
        </Box>
        </Container>
      </Dialog>
    </Container>
  );
};

export default Services;
